import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

// To remember things, components use state
// React components have state by setting this.state in their constructors 
// class Square extends React.Component {

//     // // Constructor of class -  initialises the state
//     // constructor(props) {
//     //   // Always need to call super when defining the constructor of a subclass
//     //   super(props);
//     //   this.state = {
//     //     value: null,
//     //   };
//     // }

//     render() {
//       return (
//         <button 
//           className="square" 
//           onClick={() => this.props.onClick()}
//         >
//           {this.props.value}
//         </button>
//       );
//     }
//   }

  function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>

        {props.value}
      </button>
    )
  }
  
  // Passing a prop from parent Board --> child Square.
  // This is how information flows in React apps.

  // Store the game's state in this component rather that in each square.
  // Board componenet can tell each square what to display by passing a prop. 
  class Board extends React.Component {
    // Initial state: contains array of 9 nulls corresponding to 9 squares.
    constructor(props) {
      super(props);
      this.state = {
        squares: Array(9).fill(null),
      };
    }

    handleClick(i) {
      const squares = this.state.squares.slice();
      squares[i] = 'X';
      this.setState({squares: squares});
    }
    renderSquare(i) {
      return (
        <Square 
          // Passing down two props from board to square
          value={this.state.squares[i]}

          // onClick is a function that Square can call when clicked
          onClick={() => this.handleClick(i)}
    
        />
      );
    }
  
    render() {
      const status = 'Next player: X';
  
      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }
  
  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
  
  // ========================================
  
  const root = ReactDOM.createRoot(document.getElementById("root"));
  root.render(<Game />);
